<script>
    $(function() {
        $(".owl-carousel").owlCarousel({
            margin:20,
            loop:false,
            dots: false,
            nav: false,
            animateIn: 'fadeIn',
            animateOut: 'fadeOut',
            autoplay:false,
            items: 1,
        });
    });
    moment.locale("tr");

    $('.main-sub-titles > li').hover(function () {
        $(this).siblings().removeClass('open');
        $(this).addClass('open');
        $(this).parent().parent().siblings().find('.main-dropdown-content-link.open').removeClass('open');
        var target = $('#' + $(this).data('target'));
        target.addClass('open');
    });

    function countMethod() {
        $('.counter-value').each(function(){
            $(this).prop('Counter',0).animate({
                Counter: $(this).text()
            },{
                duration: 2000,
                easing: 'swing',
                step: function (now){
                    $(this).text(Math.ceil(now).toLocaleString());
                }
            });
        });
    }

    var header = $('#main-header');
    var offset = 0;

    $(window).scroll(function () {
        if (window.pageYOffset > offset) {
            header.addClass('sticky');
            $('.mobile-menu').addClass('sticky');


        } else {
            header.removeClass('sticky');
            $('.mobile-menu').removeClass('sticky');
        }



    });

    $('.mobile-menu-button').click(function () {
        $('.mobile-menu').toggleClass('open');
    });

    //hideEmptyMenu();
    setMenuPosition();
    $('.panel-collapse').on('show.bs.collapse', function () {
        $(this).siblings('.panel-heading').addClass('active');
    });

    $('.panel-collapse').on('hide.bs.collapse', function () {
        $(this).siblings('.panel-heading').removeClass('active');
    });

    $(document).ready(function () {
        $("a[data-theme]").click(function () {
            $("head link#theme").attr("href", $(this).data("theme"));
            $(this).toggleClass('active').siblings().removeClass('active');
        });
        $("a[data-effect]").click(function () {
            $("head link#effect").attr("href", $(this).data("effect"));
            $(this).toggleClass('active').siblings().removeClass('active');
        });


    });

</script>

<script>
    function getSlickOptions() {
        return {
            dots: true,
            infinite: true,
            speed: 300,
            slidesToShow: 4,
            slidesToScroll: 4,
            prevArrow: '<button class="slide-arrow prev-arrow"></button>',
            nextArrow: '<button class="slide-arrow next-arrow"></button>',
            dotsClass: 'line-dots',
            customPaging: function (slider, i) {
                var slideNumber = (i + 1),
                    totalSlides = slider.slideCount;
                return '<a class="dot" role="button" title="' + slideNumber + ' of ' + totalSlides + '"><span class="string">' + slideNumber + '/' + totalSlides + '</span></a>';
            },
            responsive: [
                {
                    breakpoint: 1024,
                    settings: {
                        slidesToShow: 3,
                        slidesToScroll: 3,
                        infinite: true,
                        dots: true
                    }
                },
                {
                    breakpoint: 600,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 480,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }
                }
            ]
        }
    }

    $('.home-news-row, .home-announcement-row').slick(getSlickOptions());


    $('.home-event-row,.home-prizes-row,.home-library-row').slick({
        dots: true,
        infinite: false,
        speed: 300,
        slidesToShow: 5,
        slidesToScroll: 5,
        prevArrow: '<button class="slide-arrow prev-arrow"></button>',
        nextArrow: '<button class="slide-arrow next-arrow"></button>',
        dotsClass: 'line-dots',
        customPaging: function (slider, i) {
            var slideNumber = (i + 1),
                totalSlides = slider.slideCount;
            return '<a class="dot" role="button" title="' + slideNumber + ' of ' + totalSlides + '"><span class="string">' + slideNumber + '/' + totalSlides + '</span></a>';
        },
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    });

    function get_announcements(category_id) {
        $.ajax({
            url: "https://www.erbakan.edu.tr/duyuru-list",
            type: "POST",
            data: { 'category_id': category_id, '_token': 'TlQPrIL5iRNxpKPjQsJ96lojXTRTWRhFPRUH24RZ'},
            dataType: 'json',
            success: function(response){
                $('.home-announcement-row').slick('destroy');
                $('.home-announcement-row').empty();
                if (response.status == 1 && response.announcements.length > 0) {
                    $.each(response.announcements, function(key, value) {
                        if (value.important == 1) {
                            var add_important = 'announcement-important';
                        } else {
                            var add_important = '';
                        }
                        $('.home-announcement-row').append('<div class="col-md-3 col-sm-12 pointer" onclick="location.href=\''+value.link+'\';" style="">' +
                            '<div class="trend-top-img '+add_important+'">' +
                            '<div class="trend-top-cap trend-top-cap2">' +
                            '<span class="bgg">'+value.date+' '+value.month+'</span>' +
                            '<div class="home-announcement-link-style">' +
                            '<a href="'+value.link+'">'+value.title+'</a>' +
                            '</div>' +
                            '</div>' +
                            '</div>' +
                            '</div>'
                        );
                    });
                }

                $('.home-announcement-row').slick(getSlickOptions());
            }
        });
    }

    $('#announcement_category').on( "change", function() {
        var category_id  = this.value;
        get_announcements(category_id);
    });
    get_announcements(1);

</script>
